// MCP Server Example
import { Server } from '@modelcontextprotocol/sdk/server/index.js';

const server = new Server({
  name: 'my-mcp-server',
  version: '1.0.0'
});

server.setRequestHandler(ListToolsRequestSchema, async () => ({
  tools: [{
    name: 'get_weather',
    description: 'Get current weather',
    inputSchema: {
      type: 'object',
      properties: {
        location: { type: 'string' }
      }
    }
  }]
}));

// A2A Message Exchange
import { A2AClient } from '@a2a/client';

const client = new A2AClient({
  agentId: 'my-agent',
  capabilities: ['task-execution', 'data-analysis']
});

await client.sendMessage({
  to: 'target-agent-id',
  type: 'request',
  action: 'analyze-data',
  payload: { data: [...] }
});

// REST API Call
const response = await fetch('https://api.example.com/v1/data', {
  method: 'POST',
  headers: {
    'Authorization': 'Bearer ${token}',
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({ query: 'search term' })
});
const data = await response.json();

// GraphQL Query
const query = `
  query GetAgent($id: ID!) {
    agent(id: $id) {
      name
      capabilities
      status
      metrics {
        tasksCompleted
        uptime
      }
    }
  }
`;

const result = await client.query({ query, variables: { id: 'agent-123' } });

// WebSocket Connection
const ws = new WebSocket('wss://agent-server.example.com');

ws.onopen = () => {
  ws.send(JSON.stringify({
    type: 'subscribe',
    channel: 'agent-updates'
  }));
};

ws.onmessage = (event) => {
  const message = JSON.parse(event.data);
  console.log('Received:', message);
};

// gRPC Service Definition (protobuf)
service AgentService {
  rpc ExecuteTask(TaskRequest) returns (TaskResponse);
  rpc StreamResults(TaskRequest) returns (stream TaskResult);
}

message TaskRequest {
  string task_id = 1;
  string action = 2;
  map<string, string> params = 3;
}

// MQTT Publish/Subscribe
import mqtt from 'mqtt';

const client = mqtt.connect('mqtt://broker.example.com');

client.on('connect', () => {
  client.subscribe('agent/tasks/#');
  client.publish('agent/status', JSON.stringify({ state: 'online' }));
});

client.on('message', (topic, message) => {
  console.log(`Received on ${topic}:`, message.toString());
});

// JSON-RPC Request
const request = {
  jsonrpc: '2.0',
  method: 'execute_task',
  params: {
    task: 'analyze',
    data: [...]
  },
  id: 1
};

const response = await fetch('https://api.example.com/rpc', {
  method: 'POST',
  body: JSON.stringify(request)
});

// Server-Sent Events Client
const eventSource = new EventSource('https://api.example.com/stream');

eventSource.onmessage = (event) => {
  const data = JSON.parse(event.data);
  console.log('Stream update:', data);
};

eventSource.addEventListener('completion', (event) => {
  console.log('Stream completed:', event.data);
  eventSource.close();
});

// x402 Payment Flow
const response = await fetch('https://api.example.com/premium-tool', {
  headers: {
    'Authorization': 'Bearer ${token}'
  }
});

if (response.status === 402) {
  const invoice = await response.json();
  await payInvoice(invoice.payment_request);
  // Retry request with proof of payment
}
